mapper.select:
SELECT A.ID AS postId , A.ROADMAP_ID AS roadmapId , A.MEMBER_ID AS memberId , B.NICKNAME AS nickname , A.TITLE AS title , A.CREATED_AT AS createdAt , A.UPDATED_AT AS updatedAt , A.LIKE_COUNT AS likeCount , A.COMMENT_COUNT AS commentCount , A.VISIBILITY AS visibility
, EXISTS (SELECT 1 FROM LIKES B WHERE B.ALGO_POST_ID = A.ID AND B.MEMBER_ID = #{memberId} ) AS isLiked
, D.QUIZ_COUNT AS quizCount FROM ALGO_POST A LEFT JOIN MEMBER B ON A.MEMBER_ID = B.ID LEFT JOIN (SELECT C.ALGO_POST_ID , COUNT(C.ID) AS QUIZ_COUNT FROM ALGO_QUIZ_QUESTION C GROUP BY C.ALGO_POST_ID) AS D ON D.ALGO_POST_ID = A.ID
(A.TITLE LIKE CONCAT('%', #{keyword}, '%') OR A.CONTENT LIKE CONCAT('%', #{keyword}, '%'))
AND A.ROADMAP_ID = #{roadmapId}
AND A.VISIBILITY = #{visibility}
ORDER BY A.ID ASC;
